import { useState } from 'react'

function Questions() {
    const [clicado, setclicado] = useState(true)
    const cards = [
        { question: "O que é JSX?", answer: "Uma extensão da linguagem JavaScript", number: "1" },
        { question: "O React é __", number: "2", answer: "Uma biblioteca JavaScript para construção de interfaces" },
        { question: "Componentes devem iniciar com __", number: "3", answer: "Letra maiúscula" },
        { question: "Podemos colocar __ dentro do JSX", number: "4", answer: "expressões" },
    ]

    const setaStyle = {
        width: "20px",
        height: "23px"
    };

    return (

        <>
            <Question number="1" setaStyle={setaStyle} classQ="question" classA="answer" question="O que significa 'emia'?" />
            <Question number="2" setaStyle={setaStyle} question="Qual é a música da vida?" />
            <Question number="3" setaStyle={setaStyle} question="Um pêssego pode derrotar Tai Lung?" />
            <Question number="4" setaStyle={setaStyle} question="Quais são as formas de detectar um sonho lúcido?" />
        </>
    )
}

function Question(props) {
    const [clicado, setclicado] = useState(true)

    if (clicado === true) {
        return (
            <div className={props.classQ} onClick={() => setclicado(false)}>Pergunta {props.number} <img style={props.setaStyle} src="./assets/img/seta_play.png" alt="Play" /></div>

        )
    } else {
        return (
            <div className={props.classA} onClick={() => setclicado(true)}>{props.question}</div>
        )
    }
}



export default Questions
