import { useState } from 'react'

function Questions() {
    const  [clicado, setclicado] = useState(true)
    const cards = [
        { question: "O que é JSX?", answer: "Uma extensão da linguagem JavaScript", number: "1"},
        { question: "O React é __", number: "2", answer: "Uma biblioteca JavaScript para construção de interfaces" },
        { question: "Componentes devem iniciar com __", number: "3", answer: "Letra maiúscula" },
        { question: "Podemos colocar __ dentro do JSX", number: "4", answer: "expressões" },
    ]

    const setaStyle = {
      width: "20px",
      height: "23px"
    };

    if(clicado === true) {

    return (

        <> 
            <Question number="1"  setaStyle={setaStyle}/>
            {/* {cards.map((card) => <div onClick={() => setclicado(false)} className='question'>Pergunta {card.number} <img style={setaStyle} src="./assets/img/seta_play.png" alt="Play" /></div>)} */}
            {/* {cards.map((card) => <div className='question'>{card.question}</div>)} */}
        </>
    )
    } else {
        return (

            <> <Answer question="O que é JSX?" />
                {/* {cards.map((card) => <div onClick={() => setclicado(false)} className='question'>Pergunta {card.number} <img style={setaStyle} src="./assets/img/seta_play.png" alt="Play" /></div>)} */}
                {/* {cards.map((card) => <div onClick={() => setclicado(true)} className='question'>{card.question}</div>)} */}
            </>
        )
    }

}
function Question(props) {
    return(
        <div className='question' onClick={() => setclicado(true)}>Pergunta {props.number} <img style={props.setaStyle} src="./assets/img/seta_play.png" alt="Play" /></div>

    )
}

function Answer(props) {
    return(
        <div  className='question' onClick={() => setclicado(false)}>{props.question}</div>
    )
}


export default Questions
